// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const raw = $input.first().json.output[1].content[0].text

// Escape any accidental HTML characters
function escapeHtml(text) {
  return text
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;");
}

let html = escapeHtml(raw);

// Headings
html = html.replace(/^(.+)$/gm, '<p>$1</p>');

// Emojis & bullets already safe
html = html
  .replace(/üé¨/g, 'üé¨')
  .replace(/‚Äî/g, '‚Äî');

// Improve bullet formatting
html = html.replace(/^- (.*)$/gm, '<li>$1</li>');
html = html.replace(/(<li>.*<\/li>)/gs, '<ul>$1</ul>');

// Section spacing
html = html.replace(/<\/p><p>/g, '</p><br><p>');

// Wrap whole email
html = `
<div style="font-family: Arial, sans-serif; line-height:1.5; font-size:14px;">
  ${html}
</div>
`;

return {
  html
};
